import { useState } from "react";

export default function App() {
  const [billvalue, setBillValue] = useState("");

  return (
    <div>
      <BillInput />
      <PercentInput>How did you like the service</PercentInput>
      <PercentInput>How did your friend like the service</PercentInput>
      <Output />
      <Button />
    </div>
  );
}

function BillInput({ billvalue, setBillValue }) {
  return (
    <div>
      <h2>
        How much was the Bill?
        <input
          type="text"
          billvalue={billvalue}
          onChange={(e) => setBillValue(Number(e.target.value))}
          placeholder="Bill value"
        ></input>
      </h2>
    </div>
  );
}

function PercentInput({ children }) {
  const [calcAvg, setcalcAvg] = useState(0);
  const [option, setOption] = useState(0);
  return (
    <div>
      <h2>
        {children}
        <select
          value={option}
          onChange={(e) => setOption(console.log(e.target.value))}
        >
          <option value={0}>Disatisfied(0%)</option>
          <option value={5}>It was ok(5%)</option>
          <option value={10}>It was good (10%)</option>
          <option value={20}>It was amazing (20%)</option>
        </select>
      </h2>
    </div>
  );
}

function Output() {
  return (
    <div>
      <h3>{`You pay ${"x"} ($ ${billvalue} + $ ${"z"} tip)`}</h3>;
    </div>
  );
}

function Button() {
  return <button>Reset</button>;
}
