import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  // const [currOpen, setcurrOpen] = useState(null);

  return (
    <div className="app">
      <FriendList data={initialFriends} currOpen={currOpen} />
      <SpiltForm data={initialFriends} />
    </div>
  );
}
function Button({ children, onOpen }) {
  return (
    <button className="button" onOpen={onOpen}>
      {children}
    </button>
  );
}

function FriendList({ data }) {
  const [currOpen, setcurrOpen] = useState(null);

  return (
    <ul>
      <div className="sidebar">
        {data.map((friend) => (
          <Friend
            currOpen={currOpen}
            onOpen={setcurrOpen}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            id={friend.id}
          />
        ))}
      </div>
      <Button>Add friend</Button>
    </ul>
  );
}
function Friend({ name, image, id, onOpen, currOpen }) {
  // const [isformOpen, setform] = useState(false);
  const isOpen = id === currOpen;
  function handleToggle() {
    console.log("opne");
    // onOpen(isOpen ? null : id);
    // setform((cuur) => !isformOpen);
  }
  return (
    <div>
      <div>
        <li>
          <img src={image} />
          <h2>{name} </h2>
          <Button onOpen={handleToggle}>Select</Button>
        </li>
      </div>

      {isOpen && <SpiltForm />}
    </div>
  );
}

function SpiltForm() {
  return (
    <div>
      <div className="form-add-friend ">
        <form className="form-split-bill">
          <h2>split a bill with 'x'</h2>

          <label htmlFor="bill">bill Value</label>
          <input id="bill" type="text"></input>

          <label htmlFor="yourexp">your expense</label>
          <input id="yourexp" type="text"></input>

          <label htmlFor="friexp">'x' expense</label>
          <input id="friexp" type="text"></input>

          <label>whos paying the bill</label>
          <select>
            <option value="you">You</option>
            <option value="v">x</option>
          </select>

          <Button>Split Bill</Button>
        </form>
      </div>
    </div>
  );
}
