import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [showAddFrind, setShowAddFriend] = useState(false);
  const [freinds, setFriends] = useState(initialFriends);
  const [isFormOpen, setOpenForm] = useState(false);

  function handleShowAddFriend() {
    setShowAddFriend((show) => !show);
  }

  function handleAddFriends(newFriend) {
    setFriends((friends) => [...friends, newFriend]);
    setShowAddFriend(false);
  }
  // function handleSelect(id) {
  //   setOpenForm((show) => !show);
  // }
  return (
    <div className="app">
      <div className="sidebar">
        <FriendList
          freinds={freinds}
          // handleSelect={handleSelect}
          isFormOpen={isFormOpen}
        />
        {showAddFrind && <FormAddFriend onAddFriends={handleAddFriends} />}
        <Button onClick={handleShowAddFriend}>
          {showAddFrind ? "Close" : "Add Friend"}
        </Button>
      </div>
      {isFormOpen && <SpiltForm />}
    </div>
  );
}
function Button({ children, onClick }) {
  return (
    <button className="button" onClick={onClick}>
      {children}
    </button>
  );
}

function FriendList({ freinds, handleSelect, isFormOpen }) {
  return (
    <ul>
      <div>
        {freinds.map((friend) => (
          <Friend
            friend={friend}
            key={friend.id}
            handleSelect={handleSelect}
            isFormOpen={isFormOpen}
          />
        ))}
      </div>
    </ul>
  );
}
function Friend({ friend, handleSelect, isFormOpen }) {
  return (
    <div>
      <div>
        <li>
          <img src={friend.image} alt="altimg" />
          <h3>{friend.name} </h3>
          {friend.balance < 0 && (
            <p className="red">
              You owe {friend.name} $ {Math.abs(friend.balance)}
            </p>
          )}
          {friend.balance > 0 && (
            <p className="green">
              {friend.name} owns you ${Math.abs(friend.balance)}
            </p>
          )}
          {friend.balance === 0 && (
            <p className="red">You and {friend.name} are even</p>
          )}
          <Button onClick={() => handleSelect(friend.id)}>Select</Button>
        </li>
      </div>
      {isFormOpen && <SpiltForm />}
    </div>
  );
}
function FormAddFriend({ onAddFriends }) {
  const [name, setFriendName] = useState("");
  const [image, setImageUrl] = useState("https://i.pravatar.cc/48");

  function handleSubmit(e) {
    e.preventDefault();
    if (!name || !image) return;
    const id = crypto.randomUUID();
    const newFriend = {
      id,
      name,
      image: `${image}?=${id}`,
      balance: 0,
    };
    console.log(newFriend);
    onAddFriends(newFriend);
    setFriendName("");
    setImageUrl("https://i.pravatar.cc/48");
  }
  return (
    <form className="form-add-friend" onSubmit={handleSubmit}>
      <label>Friend Name</label>
      <input
        type="text"
        value={name}
        onChange={(e) => setFriendName(e.target.value)}
      />
      <label>Image URL</label>
      <input
        type="text"
        value={image}
        onChange={(e) => setImageUrl(e.target.value)}
      />
      <Button>Add friends</Button>
    </form>
  );
}
function SpiltForm() {
  return (
    <div>
      <div>
        <form className="form-split-bill">
          <h2>split a bill with 'x'</h2>

          <label htmlFor="bill">bill Value</label>
          <input id="bill" type="text"></input>

          <label htmlFor="yourexp">your expense</label>
          <input id="yourexp" type="text"></input>

          <label htmlFor="friexp">'x' expense</label>
          <input id="friexp" type="text" disabled />

          <label>whos paying the bill</label>
          <select>
            <option value="user">You</option>
            <option value="freind">x</option>
          </select>

          <Button>Split Bill</Button>
        </form>
      </div>
    </div>
  );
}
