import { useState } from "react";

export default function App() {
  const [billvalue, setBillValue] = useState("");
  const [mytip, setmyTip] = useState(0);
  const [ftip, setfTip] = useState(0);
  const avg = Number(mytip) + Number(ftip) / 2;
  console.log(avg);
  // const [tip, setTip] = useState(0);

  return (
    <div>
      <BillInput billvalue={billvalue} setBillValue={setBillValue} />
      <PercentInput tip={mytip} setTip={setmyTip}>
        How did you like the service
      </PercentInput>
      <PercentInput tip={ftip} setTip={setfTip}>
        How did your friend like the service
      </PercentInput>
      <Output billvalue={billvalue} />
      <Button />
    </div>
  );
}

function BillInput({ billvalue, setBillValue }) {
  return (
    <div>
      <h2>
        How much was the Bill?
        <input
          type="text"
          billvalue={billvalue}
          onChange={(e) => setBillValue(Number(e.target.value))}
          placeholder="Bill value"
        ></input>
      </h2>
    </div>
  );
}

function PercentInput({ children, tip, setTip }) {
  return (
    <div>
      <h2>
        {children}
        <select value={tip} onChange={(e) => setTip(Number(e.target.value))}>
          <option value={0}>Disatisfied(0%)</option>
          <option value={5}>It was ok(5%)</option>
          <option value={10}>It was good (10%)</option>
          <option value={20}>It was amazing (20%)</option>
        </select>
      </h2>
    </div>
  );
}

function Output({ billvalue }) {
  return (
    <div>
      <h3>{`You pay ${"x"} ($ ${billvalue} + $ ${"z"} tip)`}</h3>;
    </div>
  );
}

function Button() {
  return <button>Reset</button>;
}
