import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <Friend data={initialFriends} />;
    </div>
  );
}
function Button({ children, handClick }) {
  return (
    <button className="button" onClick={handClick}>
      {children}
    </button>
  );
}

function FriendList({ data }) {
  return (
    <ul>
      <Friend key={data.id} />;
    </ul>
  );
}
function Friend() {
  function handClick() {
    console.log("sdasj");
  }
  return (
    <div className="sidebar">
      {initialFriends.map((friend) => (
        <li>
          <img src={friend.image} />
          <h2>{friend.name} </h2>
          <Button handClick={handClick}>Select</Button>
        </li>
      ))}
      <Button>Add friend</Button>
    </div>
  );
}
