import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <FriendList data={initialFriends} />;
    </div>
  );
}
function Button({ children, handClick }) {
  return (
    <button className="button" onClick={handClick}>
      {children}
    </button>
  );
}

function FriendList({ data, name }) {
  return (
    <div className="sidebar">
      {data.map((friend) => (
        <Friend data={initialFriends} key={friend.id} name={friend.name} />
      ))}
    </div>
  );
}
function Friend({ data }) {
  return (
    <div>
      <li>
        <img src={data.image} />
        <h2>{data.name} </h2>
        <Button>Select</Button>
      </li>
      <Button>Add friend</Button>
    </div>
  );
}
