import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  return (
    <div className="app">
      <FriendList data={initialFriends} />
      <SpiltForm />
    </div>
  );
}
function Button({ children, handClick }) {
  return (
    <button className="button" onClick={handClick}>
      {children}
    </button>
  );
}

function FriendList({ data }) {
  return (
    <ul>
      <div className="sidebar">
        {data.map((friend) => (
          <Friend key={friend.id} name={friend.name} image={friend.image} />
        ))}
      </div>
      <Button>Add friend</Button>
    </ul>
  );
}
function Friend({ name, image }) {
  return (
    <div>
      <li>
        <img src={image} />
        <h2>{name} </h2>
        <Button>Select</Button>
      </li>
    </div>
  );
}

function SpiltForm() {
  return (
    <div className="form-split-bill">
      <h2>split a bill with 'x'</h2>
      <label for="bill">bill Value</label>
      <input id="bill" type="text" placeholder="bill value"></input>
      <label for="bill">bill Value</label>
      <input id="bill" type="text" placeholder="bill value"></input>
      <label for="bill">bill Value</label>
      <input id="bill" type="text" placeholder="bill value"></input>
    </div>
  );
}
