import { useState } from "react";
const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [isformOpen, setform] = useState(false);
  function handSelect(name) {
    console.log(name);
  }
  return (
    <div className="app">
      <FriendList data={initialFriends} handSelect={handSelect} />
      <SpiltForm
        isformOpen={isformOpen}
        setform={setform}
        data={initialFriends}
      />
    </div>
  );
}
function Button({ children, handSelect }) {
  return (
    <button className="button" onClick={handSelect}>
      {children}
    </button>
  );
}

function FriendList({ data, handSelect }) {
  return (
    <ul>
      <div className="sidebar">
        {data.map((friend) => (
          <Friend
            handSelect={handSelect}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            id={friend.id}
          />
        ))}
      </div>
      <Button>Add friend</Button>
    </ul>
  );
}
function Friend({ name, image, id, handSelect }) {
  return (
    <div>
      <li>
        <img src={image} />
        <h2>{name} </h2>
        <Button handSelect={() => handSelect(name)}>Select</Button>
      </li>
    </div>
  );
}

function SpiltForm({ isformOpen, setform, handSelect }) {
  return (
    <div>
      {isformOpen && (
        <div className="form-add-friend ">
          <form className="form-split-bill">
            <h2>split a bill with 'x'</h2>

            <label for="bill">bill Value</label>
            <input id="bill" type="text"></input>

            <label for="yourexp">your expense</label>
            <input id="yourexp" type="text"></input>

            <label for="friexp">'x' expense</label>
            <input id="friexp" type="text"></input>

            <label>whos paying the bill</label>
            <select>
              <option value="you">You</option>
              <option value="v">x</option>
            </select>

            <Button>Split Bill</Button>
          </form>
        </div>
      )}
    </div>
  );
}
