import { useState } from "react";

export default function App() {
  return (
    <div>
      <BillInput />
      <PercentInput />
    </div>
  );
}

function BillInput() {
  return (
    <div>
      <h2>
        How much was the Bill? <input type="text"></input>
      </h2>
    </div>
  );
}

function PercentInput() {
  const [option, setOption] = useState("dis");
  return (
    <div>
      <h2>
        How did you like the service?
        <select value={option} onChange={setOption}>
          <optin value="dis">Disatisfied(0%)</optin>
          <optin value="ok">It was ok(5%)</optin>
          <optin value="good">It was good (10%)</optin>
          <optin value="amazing">It was amazing (20%)</optin>
        </select>
      </h2>
    </div>
  );
}
