import { useState } from "react";

export default function App() {
  return (
    <div>
      <BillInput />
      <PercentInput />
    </div>
  );
}

function BillInput() {
  return (
    <div>
      <h2>
        How much was the Bill? <input type="text"></input>
      </h2>
    </div>
  );
}

function PercentInput() {
  return (
    <div>
      <h2>
        How did you like the service{" "}
        <select value>
          <optin value="dis">Disatisfied(0%)</optin>
          <optin value="ok">It was ok</optin>
          <optin value="good">It was good</optin>
          <optin value="amazing">It was amazing</optin>
        </select>
      </h2>
    </div>
  );
}
