import { useState } from "react";

export default function App() {
  return (
    <div>
      <BillInput />
      <PercentInput>How did you like the service</PercentInput>
      <PercentInput>How did your friend like the service</PercentInput>
      <Output />
    </div>
  );
}

function BillInput() {
  return (
    <div>
      <h2>
        How much was the Bill?
        <input type="text" placeholder="Bill value"></input>
      </h2>
    </div>
  );
}

function PercentInput({ children }) {
  const [option, setOption] = useState("dis");
  return (
    <div>
      <h2>
        {children}
        <select
          value={option}
          onChange={(e) => setOption(Number(e.target.value))}
        >
          <option value="dis">Disatisfied(0%)</option>
          <option value="ok">It was ok(5%)</option>
          <option value="good">It was good (10%)</option>
          <option value="amazing">It was amazing (20%)</option>
        </select>
      </h2>
    </div>
  );
}

function Output() {
  return (
    <div>
      <p>{`You pay ${"x"} ($ ${"y"} + $ ${"z"} tip)`}</p>;
    </div>
  );
}

function Button() {
  return <button>Reset</button>;
}
